export const getLocalTimezone = () => Intl.DateTimeFormat().resolvedOptions().timeZone;
export const DEFAULT_TIMEZONES_CONFIG = {
    currentTimezone: '',
    listOfTimezones: DEFAULT_LIST_OF_TIME_ZONES,
};
// Heavy initial timezones calculations
export const initTimezones = (timezones = DEFAULT_LIST_OF_TIME_ZONES, workersDisabled) => {
    const now = Date.now();
    const lsTimezonesCache = localStorage.getItem(LOCALSTORAGE_TIMEZONES_KEY);
    if (lsTimezonesCache) {
        const parsedCache = JSON.parse(lsTimezonesCache);
        if (now > parsedCache.expireTimeStamp ||
            // comparing by STRING LENGTH cached timezones with current argument
            !parsedCache.timezonesRaw ||
            JSON.stringify(timezones).length !== parsedCache.timezonesRaw.length) {
            localStorage.removeItem(LOCALSTORAGE_TIMEZONES_KEY);
        }
        else {
            return Promise.resolve(parsedCache.timezones);
        }
    }
    try {
        if (workersDisabled) {
            // we need to throw it explicitly, bcs if code reaches line with creating worker, then
            // catch won't work in vite/rollup
            throw new Error('Workers disabled');
        }
        const worker = wrap(new Worker(new URL('./timezones-worker.js', import.meta.url)));
        const result = worker.parseTimezones(timezones).then(({ tzData, timezones }) => {
            saveTimeZonesToLS(tzData, timezones);
            return tzData;
        }, e => {
            console.error('Something went wrong', e);
            const tzData = parseTimezones(timezones);
            saveTimeZonesToLS(tzData, timezones);
            return tzData;
        });
        return result;
    }
    catch (e) {
        // eslint-disable-next-line @typescript-eslint/restrict-template-expressions
        console.warn(`Failed to calculate in worker: ${e}`);
        const parsed = parseTimezones(timezones);
        saveTimeZonesToLS(parsed, timezones);
        return Promise.resolve(parsed);
    }
};
